import React from 'react'
import Header from './components/Header'
import Footer from './components/Footer'
import Items from './components/Items'
import InfoItem from './components/InfoItem'


class App extends React.Component {
    constructor(props) {
        super(props)
        this.state =  {
            orders: [],
            items: [
                {
                    id: 1,
                    title: 'Ракетка',
                    img: 'racket.jpg',
                    desc: 'Ракетка для настольного тенниса Torneo Champion Speed станет отличным выбором для игроков с поставленным ударом, которые предпочитают атакующий стиль и скоростную игру и уже достигли высокого уровня мастерства. Основание выполнено из семислойной фанеры толщиной 6,5 мм. Вес ракетки составляет 180 грамм.',
                    category: 'Настольный тенис',
                    price: '2950'
                },
                {   
                    id: 2,
                    title: 'Мяч',
                    img: 'ball.jpg',
                    desc: 'Мяч для настольного тенниса Torneo Expert 3 Star предназначен для профессиональных соревнований и тренировок. Мяч имеет однородную поверхность и высокую отскокоспособность. Диаметр мяча составляет 40 мм. В упаковке 6 штук.',
                    category: 'Настольный тенис',
                    price: '450' 
                },
                {
                    id: 3,
                    title: 'Сетка',
                    img: 'net.jpg',
                    desc: 'Сетка для настольного тенниса Torneo Pro Net сделана из высококачественного нейлона и имеет регулируемую высоту и натяжение. Сетка легко крепится к столу с помощью металлических зажимов. Длина сетки составляет 183 см.',
                    category: 'Настольный тенис',
                    price: '1250'
                }
            ],
            infoItem: false,

        }
        this.addToOrder = this.addToOrder.bind(this)
        this.deleteFromOrder = this.deleteFromOrder.bind(this)
        this.showItem = this.showItem.bind(this)
    }
    render() {
    return (
        <div className='wrapper'>
            <Header orders={this.state.orders} onDelete={this.deleteFromOrder}/>
            <Items showItem={this.showItem} items={this.state.items} onAdd={this.addToOrder}/>

            {this.state.infoItem && <InfoItem /> }
            <Footer />
        </div>
    )
    }

    showItem() {
        this.setState({infoItem: !this.state.infoItem})
    }

    addToOrder(item) {
        let inArray = false
        this.state.orders.forEach(el => {
            if (el.id === item.id) {
                inArray = true;
            }
        })
        if (!inArray) {
            this.setState({orders: [...this.state.orders, item]})
        }
    }

    deleteFromOrder(id) {
        this.setState({orders: this.state.orders.filter(el => el.id !== id)})
    }
}

export default App;
