// Подключаем библиотеку react для создания компонентов
import React, { useState, useEffect } from 'react';
// Подключаем библиотеку axios для отправки запросов к серверу
import axios from 'axios';
// Подключаем компонент Link для навигации между страницами
import { Link } from 'react-router-dom';

import './Home.css';

// Создаем компонент Home
function Home() {
  // Создаем состояние для хранения списка товаров
  const [products, setProducts] = useState([]);
  // Создаем состояние для хранения корзины
  const [cart, setCart] = useState([]);
  // Создаем эффект для получения списка товаров с сервера при первом рендеринге компонента
  useEffect(() => {
    // Отправляем GET-запрос к серверу по адресу /products
    axios.get('http://localhost:3001/products')
      .then(res => {
        // Если запрос успешен, обновляем состояние products с помощью функции setProducts
        setProducts(res.data);
      })
      .catch(err => {
        // Если запрос неуспешен, выводим сообщение об ошибке в консоль
        console.error(err);
      });
  }, []); // Передаем пустой массив в качестве зависимости, чтобы эффект сработал только один раз
  // Создаем функцию для добавления товара в корзину
  const addToCart = product => {
    // Проверяем, есть ли уже такой товар в корзине
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      // Если есть, то увеличиваем его количество на единицу
      existingItem.quantity++;
      // Обновляем состояние корзины с помощью функции setCart
      setCart([...cart]);
    } else {
      // Если нет, то добавляем новый элемент в корзину с количеством равным единице
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };
  // Возвращаем JSX-разметку для отображения главной страницы с каталогом товаров и корзиной
  return (
    <div className="home">
      <h1>Магазин спортивных товаров</h1>
      <div className="products">
        <h2>Каталог товаров</h2>
        <ul>
          {products.map(product => ( // Для каждого товара из списка products создаем элемент списка с его названием, ценой и кнопкой добавления в корзину
            <li key={product.id}>
              <h3>{product.name}</h3>
              <p>{product.price} руб.</p>
              <button onClick={() => addToCart(product)}>Добавить в корзину</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="cart">
        <h2>Корзина</h2>
        <ul>
          {cart.map(item => ( // Для каждого элемента из корзины создаем элемент списка с его названием, ценой, количеством и общей стоимостью
            <li key={item.id}>
              <h3>{item.name}</h3>
              <p>{item.price} руб. x {item.quantity} = {item.price * item.quantity} руб.</p>
            </li>
          ))}
        </ul>
        <p>Итого: {cart.reduce((total, item) => total + item.price * item.quantity, 0)} руб.</p> // Считаем и выводим общую стоимость всех товаров в корзине
        {cart.length > 0 && ( // Если корзина не пуста, то выводим ссылку для перехода на страницу оформления заказа
          <Link to="/cart">Оформить заказ</Link>
        )}
      </div>
    </div>
  );
}
// Экспортируем компонент Home для использования в других файлах
export default Home;
