// Подключаем библиотеку react для создания компонентов
import React, { useState, useEffect } from 'react';
// Подключаем библиотеку axios для отправки запросов к серверу
import axios from 'axios';
// Подключаем компонент Link для навигации между страницами
import { Link } from 'react-router-dom';
// Создаем компонент Order
function Order(props) {
  // Получаем идентификатор заказа из пропсов
  const { id } = props.match.params;
  // Создаем состояние для хранения информации о заказе
  const [order, setOrder] = useState(null);
  // Создаем эффект для получения информации о заказе с сервера при первом рендеринге компонента
  useEffect(() => {
    // Отправляем GET-запрос к серверу по адресу /order/:id с идентификатором заказа в параметре
    axios.get('http://localhost:3001/order/' + id)
      .then(res => {
        // Если запрос успешен, обновляем состояние order с помощью функции setOrder
        setOrder(res.data);
      })
      .catch(err => {
        // Если запрос неуспешен, выводим сообщение об ошибке в консоль
        console.error(err);
      });
  }, [id]); // Передаем идентификатор заказа в качестве зависимости, чтобы эффект сработал только при изменении этого значения
  // Возвращаем JSX-разметку для отображения страницы с информацией о заказе
  return (
    <div className="order">
      <h1>Информация о заказе</h1>
      {order ? ( // Если информация о заказе есть, то выводим ее в виде списка
        <ul>
          <li>Номер заказа: {order.id}</li>
          <li>Имя клиента: {order.name}</li>
          <li>Электронная почта: {order.email}</li>
          <li>Телефон: {order.phone}</li>
          <li>Адрес доставки: {order.address}</li>
          <li>Список товаров:</li>
          <ul>
            {order.items.map(item => ( // Для каждого товара из списка items создаем элемент списка с его названием, ценой, количеством и общей стоимостью
              <li key={item.id}>
                <h3>{item.name}</h3>
                <p>{item.price} руб. x {item.quantity} = {item.price * item.quantity} руб.</p>
              </li>
            ))}
          </ul>
          <li>Итого: {order.items.reduce((total, item) => total + item.price * item.quantity, 0)} руб.</li> // Считаем и выводим общую стоимость всех товаров в заказе
        </ul>
      ) : ( // Если информации о заказе нет, то выводим сообщение о том, что она загружается
        <p>Загрузка...</p>
      )}
      <Link to="/">Вернуться на главную</Link> // Создаем ссылку для перехода на главную страницу
    </div>
  );
}
// Экспортируем компонент Order для использования в других файлах
export default Order;
