// Подключаем библиотеку react для создания компонентов
import React from 'react';
// Подключаем библиотеку react-router-dom для роутинга
import { BrowserRouter, Switch, Route } from 'react-router-dom';
// Подключаем компоненты для отображения разных страниц
import Home from './Home';
import Cart from './Cart';
import Order from './Order';
// Создаем главный компонент App
function App() {
  // Возвращаем JSX-разметку для роутера и переключателя между компонентами
  return (
    <BrowserRouter>
      <Switch>
        <Route exact path="/" component={Home} /> // Если адрес страницы - корень, то отображаем компонент Home
        <Route path="/cart" component={Cart} /> // Если адрес страницы - /cart, то отображаем компонент Cart
        <Route path="/order/:id" component={Order} /> // Если адрес страницы - /order/:id, то отображаем компонент Order с параметром id
      </Switch>
    </BrowserRouter>
  );
}
// Экспортируем компонент App для использования в других файлах
export default App;
