import React, { useState, useEffect, useContext } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Row, Col, Image, Card, Button, Form } from 'react-bootstrap';
import axios from 'axios';
import { AppContext } from '../context/AppContext.js';

const ProductDetail = () => {
  // get the product id from the url params
  const { id } = useParams();
  // get the history object for navigation
  const history = useHistory();
  // get the context data
  const { cart, setCart } = useContext(AppContext);
  // create a state variable for product
  const [product, setProduct] = useState(null);
  // create a state variable for quantity
  const [quantity, setQuantity] = useState(1);

  // fetch product details from the server API
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        // get the response from the server
        const { data } = await axios.get(`/api/products/${id}`);
        // set the product state
        setProduct(data);
      } catch (error) {
        // handle any errors
        console.error(error);
      }
    };

    // call the fetch function
    fetchProduct();
  }, [id]);

  // handle quantity change
  const handleQuantityChange = (e) => {
    // get the value of the input
    const { value } = e.target;
    // update the quantity state
    setQuantity(value);
  };

  // handle add to cart button click
  const handleAddToCart = () => {
    // check if the product is already in the cart
    const existingItem = cart.find((item) => item.product_id === product.id);
    if (existingItem) {
      // update the quantity of the existing item
      existingItem.quantity += Number(quantity);
    } else {
      // create a new item with the product and quantity data
      const newItem = {
        product_id: product.id,
        name: product.name,
        price: product.price,
        image: product.image,
        quantity: Number(quantity),
      };
      // add the new item to the cart
      cart.push(newItem);
    }
    // update the cart state
    setCart([...cart]);
    // navigate to the cart page
    history.push('/cart');
  };

  return (
    <div>
      <h1>Product Detail</h1>
      {product ? (
        <Row>
          <Col md={6}>
            <Image src={product.image} alt={product.name} fluid />
          </Col>
          <Col md={3}>
            <Card>
              <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                <Card.Text>{product.description}</Card.Text>
                <Card.Text>Price: ${product.price}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3}>
            <Card>
              <Card.Body>
                <Form.Group controlId="quantity">
                  <Form.Label>Quantity</Form.Label>
                  <Form.Control
                    type="number"
                    value={quantity}
                    onChange={handleQuantityChange}
                    min="1"
                    max="10"
                  />
                </Form.Group>
                <Button variant="primary" onClick={handleAddToCart}>
                  Add to Cart
                </Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductDetail;
