import React, { useState, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';
import { AppContext } from '../context/AppContext';

const Checkout = () => {
  // get the history object for navigation
  const history = useHistory();
  // get the context data
  const { user, cart } = useContext(AppContext);
  // create state variables for form data
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [country, setCountry] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [cardName, setCardName] = useState('');
  const [cardExpiry, setCardExpiry] = useState('');
  const [cardCvv, setCardCvv] = useState('');

  // handle form submit
  const handleSubmit = async (e) => {
    // prevent default browser behavior
    e.preventDefault();
    try {
      // create an order object with the form data and the user and cart data
      const order = {
        user_id: user.id,
        total: cart.reduce(
          (acc, item) => acc + item.price * item.quantity,
          0
        ),
        status: 'pending',
        address,
        city,
        country,
        card_number: cardNumber,
        card_name: cardName,
        card_expiry: cardExpiry,
        card_cvv: cardCvv,
        order_items: cart.map((item) => ({
          product_id: item.product_id,
          quantity: item.quantity,
          price: item.price,
        })),
      };
      // send a post request to the server API to create a new order
      await axios.post('/api/orders', order);
      // navigate to the confirmation page
      history.push('/confirmation');
    } catch (error) {
      // handle any errors
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Checkout</h1>
      <Form onSubmit={handleSubmit}>
        <h3>Shipping Details</h3>
        <Form.Group controlId="address">
          <Form.Label>Address</Form.Label>
          <Form.Control
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="city">
          <Form.Label>City</Form.Label>
          <Form.Control
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="country">
          <Form.Label>Country</Form.Label>
          <Form.Control
            type="text"
            value={country}
            onChange={(e) => setCountry(e.target.value)}
            required
          />
        </Form.Group>
        <h3>Payment Details</h3>
        <Form.Group controlId="cardNumber">
          <Form.Label>Card Number</Form.Label>
          <Form.Control
            type="text"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="cardName">
          <Form.Label>Card Name</Form.Label>
          <Form.Control
            type="text"
            value={cardName}
            onChange={(e) => setCardName(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="cardExpiry">
          <Form.Label>Card Expiry</Form.Label>
          <Form.Control
            type="text"
            value={cardExpiry}
            onChange={(e) => setCardExpiry(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="cardCvv">
          <Form.Label>Card CVV</Form.Label>
          <Form.Control
            type="text"
            value={cardCvv}
            onChange={(e) => setCardCvv(e.target.value)}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Place Order
        </Button>
      </Form>
    </div>
  );
};

export default Checkout;
