// Подключаем библиотеку react для создания компонентов
import React from 'react';
// Подключаем библиотеку react-router-dom для роутинга
import { BrowserRouter, Routes, Route } from 'react-router-dom';
// Подключаем компоненты для отображения разных страниц
import Home from './Home';
import Cart from './Cart';
import Order from './Order';
// Создаем главный компонент App
function App() {
  // Возвращаем JSX-разметку для роутера и переключателя между компонентами
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} /> // Если адрес страницы - корень, то отображаем компонент Home
        <Route path="/cart" element={<Cart />} /> // Если адрес страницы - /cart, то отображаем компонент Cart
        <Route path="/order/:id" element={<Order />} /> // Если адрес страницы - /order/:id, то отображаем компонент Order с параметром id
      </Routes>
    </Router>
  );
}
// Экспортируем компонент App для использования в других файлах
export default App;
