// Подключаем библиотеку react для создания компонентов
import React, { useState } from 'react';
// Подключаем библиотеку axios для отправки запросов к серверу
import axios from 'axios';
// Подключаем компонент Link для навигации между страницами
import { Link } from 'react-router-dom';
// Создаем компонент Cart
function Cart(props) {
  // Получаем корзину из пропсов
  const { cart } = props;
  // Создаем состояние для хранения данных о заказе
  const [order, setOrder] = useState({
    name: '', // Имя клиента
    email: '', // Электронная почта клиента
    phone: '', // Телефон клиента
    address: '', // Адрес доставки
    items: cart // Список товаров в заказе
  });
  // Создаем функцию для обработки изменения полей ввода данных о заказе
  const handleChange = e => {
    // Получаем имя и значение поля из события
    const { name, value } = e.target;
    // Обновляем состояние заказа с помощью функции setOrder
    setOrder({ ...order, [name]: value });
  };
  // Создаем функцию для обработки отправки формы оформления заказа
  const handleSubmit = e => {
    // Предотвращаем стандартное поведение формы (перезагрузку страницы)
    e.preventDefault();
    // Отправляем POST-запрос к серверу по адресу /order с данными о заказе в теле запроса
    axios.post('http://localhost:3000/products.json', order)
      .then(res => {
        // Если запрос успешен, перенаправляем пользователя на страницу с информацией о заказе с помощью пропса history
        props.history.push('/order/' + res.data.insertId);
      })
      .catch(err => {
        // Если запрос неуспешен, выводим сообщение об ошибке в консоль
        console.error(err);
      });
  };
  // Возвращаем JSX-разметку для отображения страницы с корзиной и формой оформления заказа
  return (
    <div className="cart">
      <h1>Корзина</h1>
      <ul>
        {cart.map(item => ( // Для каждого элемента из корзины создаем элемент списка с его названием, ценой, количеством и общей стоимостью
          <li key={item.id}>
            <h3>{item.name}</h3>
            <p>{item.price} руб. x {item.quantity} = {item.price * item.quantity} руб.</p>
          </li>
        ))}
      </ul>
      <p>Итого: {cart.reduce((total, item) => total + item.price * item.quantity, 0)} руб.</p> // Считаем и выводим общую стоимость всех товаров в корзине
      <form onSubmit={handleSubmit}> // Создаем форму для ввода данных о заказе и отправки их на сервер
        <label htmlFor="name">Имя:</label>
        <input type="text" id="name" name="name" value={order.name} onChange={handleChange} required /> // Создаем поле ввода для имени клиента с обязательным заполнением и связываем его со состоянием заказа
        <label htmlFor="email">Электронная почта:</label>
        <input type="email" id="email" name="email" value={order.email} onChange={handleChange} required /> // Создаем поле ввода для электронной почты клиента с обязательным заполнением и связываем его со состоянием заказа
        <label htmlFor="phone">Телефон:</label>
        <input type="tel" id="phone" name="phone" value={order.phone} onChange={handleChange} required /> // Создаем поле ввода для телефона клиента с обязательным заполнением и связываем его со состоянием заказа
        <label htmlFor="address">Адрес доставки:</label>
        <input type="text" id="address" name="address" value={order.address} onChange={handleChange} required /> // Создаем поле ввода для адреса доставки с обязательным заполнением и связываем его со состоянием заказа
        <button type="submit">Оформить заказ</button> // Создаем кнопку для отправки формы на сервер
      </form>
      <Link to="/">Вернуться на главную</Link> // Создаем ссылку для перехода на главную страницу
    </div>
  );
}
// Экспортируем компонент Cart для использования в других файлах
export default Cart;
